var DBClient = function (record) {
	// this record is the only one showed
	this.record = record;
};
DBClient.prototype.createCollection = function (collection, fields, assocs) {
	// nothing needed
};
DBClient.prototype.selectRecords = function (collection, config) {
	if (!config.limit) config.limit = Math.round(Math.random() * 10) + 1;
	
	var records = [];
	for (var i = 0; i < config.limit; i++) {
		var record = this.record;
		record.id = Math.round(Math.random() * 100) + 1;
		
		records.push(record);
	}
	
	config.callback(null, records);
};
DBClient.prototype.clearRecords = function (collection, config, callback) {
	config.callback();
};
DBClient.prototype.saveRecord = function (collection, data, callback) {
	callback(null, Math.round(Math.random() * 100) + 1);
};

exports.connect = function (options, callback) {
	callback(true, new DBClient({
		"name"	: "John Doe",
		"age"	: 20
	}));
};
